import React, { useEffect, useState } from "react";
import { projectsData } from "../data/galleryData";
import Card from "./Card";

const Project = () => {
  const [projects] = useState(projectsData);
  const [rangeValue, setRangeValue] = useState(36);
  const [selectedRadio, setSelectedRadio] = useState("");
  const radios = [
    "Mariage",
    "Grossesse",
    "Bébé",
    "Famille",
    "Baptême",
    "Couple",
  ];

  return (
    <main>
      <div className="filter-container">
        <ul className="radio-container">
          <input
            type="range"
            min="1"
            max="250"
            defaultValue={rangeValue}
            onChange={(e) => setRangeValue(e.target.value)}
          />
          {radios.map((cat) => (
            <li>
              <input
                type="radio"
                id={cat}
                name="catRadio"
                checked={cat === selectedRadio}
                onClick={(e) => setSelectedRadio(e.target.id)}
              />
              <label htmlFor="catRadio">{cat}</label>
            </li>
          ))}
        </ul>
        {selectedRadio && (
          <button onClick={() => setSelectedRadio("")}>
            Annuler la recherche
          </button>
        )}
      </div>

      <div className="gallery">
        {projects
          // .filter((project) => project.title === selectedRadio)
          .filter((project) => selectedRadio.includes(project.title))
          .map((project) => (
            <Card projectNumber={project.id} idCat={project.idCat} />
          ))}
      </div>
    </main>
  );
};

export default Project;
