import React, { useEffect, useState } from "react";
import { projectsData } from "../data/projectsData";
import { catData } from "../data/catData";
import Card from "./Card";

const Project = () => {
  // const [sortMethod, setSortMethod] = useState("");
  const [projects] = useState(projectsData);
  const [cats] = useState(catData);
  const [selectedRadio, setSelectedRadio] = useState("");
  const [rangeValue, setRangeValue] = useState(36);
  const radios = ["bébé", "mariage"];
  // let [projectNumber, setProjectnumber] = useState("");
  // const projectpos = [];

  // for (let i = 0; i < projects.length; i++) {
  //   projectpos.push(i);
  // }

  // console.log(projectDisplay());
  return (
    <main>
      {/* <div className="button-container">
        <button
          id="1"
          className="button"
          onClick={(e) => setSortMethod(e.target.id)}
        >
          bébé
        </button>
        <button
          id="2"
          className="button"
          onClick={(e) => setSortMethod(e.target.id)}
        >
          Mariage
        </button>
      </div> */}
      <ul className="radio-container">
        <input
          type="range"
          min="1"
          max="250"
          defaultValue={rangeValue}
          onChange={(e) => setRangeValue(e.target.value)}
        />
        {cats.map((cat) => (
          <li>
            <input
              type="radio"
              id={cat.cat}
              name="catRadio"
              checked={cat.cat === selectedRadio}
              onChange={(e) => setSelectedRadio(e.target.id)}
            />
            <label htmlFor="cat">{cat.cat}</label>
          </li>
        ))}
      </ul>

      {selectedRadio && (
        <button onClick={() => setSelectedRadio("")}>
          Annuler la recherche
        </button>
      )}

      <div className="gallery">
        {projects
          .filter((project) => project.title.includes(selectedRadio))
          .map((project) => (
            <Card projectNumber={project.id} idCat={project.idCat} />
          ))}
      </div>
    </main>
  );
};

export default Project;
