import React, { useEffect, useState } from "react";
import { galleryData } from "../data/galleryData";
import Card from "./Card";
import Filter from "./Filter";

const GalleryDisplay = () => {
  const [gallery] = useState(galleryData);
  const [rangeValue, setRangeValue] = useState(36);
  const [selectedRadio, setSelectedRadio] = useState("");
  const radios = [
    "Mariage",
    "Grossesse",
    "Bébé",
    "Famille",
    "Baptême",
    "Couple",
    "Portrait",
  ];

  return (
    <main>
      <div className="filter-container">
        <ul className="radio-container">
          <input
            type="range"
            min="1"
            max="250"
            defaultValue={rangeValue}
            onChange={(e) => setRangeValue(e.target.value)}
          />

          {radios.map((cat) => (
            <li>
              <input
                type="radio"
                id={cat}
                name="catRadio"
                checked={cat === selectedRadio}
                onClick={(e) => setSelectedRadio(e.target.id)}
              />
              <label htmlFor="catRadio">{cat}</label>
            </li>
          ))}
        </ul>

        {selectedRadio && (
          <button onClick={() => setSelectedRadio("")}>
            Annuler la recherche
          </button>
        )}
      </div>
      <div className="gallery">
        {gallery
          .filter((gal) => gal.cat === selectedRadio)
          // .filter((gal) => gal.cat === input.id)
          // .filter((gal) => selectedRadio.includes(gal.cat))
          .map((gal) => (
            <Card galleryNumber={gal.id} idCat={gal.cat} />
          ))}
      </div>
    </main>
  );
};

export default GalleryDisplay;
